import React, { useState } from 'react';

import './pagination.scss';

interface PaginationProps {
  itemsPerPage: number;
}

export const Pagination: React.FunctionComponent<PaginationProps> = ({
  itemsPerPage,
  children,
}) => {
  const [currentPage, setCurrentPage] = useState(0);
  const items = React.Children.toArray(children);

  // If items divide perfectly then we dont need last page
  const finalPage =
    items.length % itemsPerPage === 0
      ? Math.floor(items.length / itemsPerPage) - 1
      : Math.floor(items.length / itemsPerPage);

  const prevActive = currentPage !== 0;
  const nextActive = currentPage !== finalPage;
  return items.length > 0 ? (
    <div className="pagination">
      <div className="pagination-items">
        {items.slice(
          itemsPerPage * currentPage,
          itemsPerPage * (currentPage + 1)
        )}
      </div>
      <nav
        className="pagination-controls"
        role="navigation"
        aria-label="Pagination navigation">
        <div
          className={`pagination-button back ${
            prevActive ? 'active' : 'disabled'
          }`}>
          <button onClick={() => prevActive && setCurrentPage(currentPage - 1)}>
            <span className="icon back-arrow"></span>
            <span className="sr">Back</span>
          </button>
        </div>
        <div
          className={`pagination-button next ${
            nextActive ? 'active' : 'disabled'
          }`}>
          <button onClick={() => nextActive && setCurrentPage(currentPage + 1)}>
            <span className="icon next-arrow"></span>
            <span className="sr">Next</span>
          </button>
        </div>
      </nav>
    </div>
  ) : null;
};
